def TAU = 2*Math.PI;
def IGNORE_BALL_COLOR = false;

enum BallColor {BLUE, RED, YELLOW, GREEN};
def BALL_COLOR_VALUES = BallColor.values();

enum MultiballStatus {NOT_READY, READY, STARTING, ACTIVE};

def JACKPOT_BASE_SCORE = 50000;
def RAMP_BASE_SCORE = 5000;

def BALL_PRIMARY_COLORS = [
  (BallColor.BLUE): Color.fromRGB(0x66, 0x88, 0xEE),
  (BallColor.RED): Color.fromRGB(0xEE, 0x88, 0x88),
  (BallColor.YELLOW): Color.fromRGB(0xCC, 0xCC, 0x77),
  (BallColor.GREEN): Color.fromRGB(0x77, 0xDD, 0x77),
];
def BALL_SECONDARY_COLORS = [
  (BallColor.BLUE): Color.fromRGB(0x44, 0x66, 0xCC),
  (BallColor.RED): Color.fromRGB(0xCC, 0x66, 0x66),
  (BallColor.YELLOW): Color.fromRGB(0xAA, 0xAA, 0x55),
  (BallColor.GREEN): Color.fromRGB(0x55, 0xBB, 0x55),
];
def WALL_COLORS = [
  (BallColor.BLUE): Color.fromRGB(0x00, 0x66, 0xFF),
  (BallColor.RED): Color.fromRGB(0xCC, 0x00, 0x00),
  (BallColor.YELLOW): Color.fromRGB(0xCC, 0xCC, 0x00),
  (BallColor.GREEN): Color.fromRGB(0x00, 0xCC, 0x00),
];
def DISABLED_ROLLOVER_COLORS = [
  (BallColor.BLUE): Color.fromRGB(0x00, 0x33, 0x66),
  (BallColor.RED): Color.fromRGB(0x66, 0x00, 0x00),
  (BallColor.YELLOW): Color.fromRGB(0x66, 0x66, 0x00),
  (BallColor.GREEN): Color.fromRGB(0x00, 0x66, 0x00),
];


// Initialized in gameStarted.
def centerRolloversByColor = null;
def centerLinesByColor = null;

def launchBarrier = null;

def rampBonuses = null;

def extraBallBarriers = null;
def extraBallRollover = null;

// Rotating triangle.
def triangleRotationSpeed = 1;
def triangleRotationBaseSpeed = 1.0 // radians/sec
def triangleRotationAngle = 0.0;
// These should be computed.
def triangleCenter = [10, 18.86];
def triangleWalls = null;
// Equilateral triangle with sides of length 2. Base is sqrt(3),
// center is 2/3 from vertex to base.
def triangleRadius = Math.sqrt(3.0) * 2/3;
def triangleCenterRollover = null;

def multiballStatus = MultiballStatus.NOT_READY;
def multiballJackpotCount = 0;

def setBallColor = {ball, color ->
  ball.setPrimaryColor(BALL_PRIMARY_COLORS[color]);
  ball.setSecondaryColor(BALL_SECONDARY_COLORS[color]);
};

def getBallColor = {ball ->
  def ballPrimaryColor = ball.getPrimaryColor();
  for (def ballColor : BALL_COLOR_VALUES) {
    if (ballPrimaryColor == BALL_PRIMARY_COLORS[ballColor]) {
      return ballColor;
    }
  }
};

def hasBallWithColor = {field, ballColor ->
  for (def ball : field.getBalls()) {
    if (getBallColor(ball) == ballColor) return true;
  }
  return false;
};

def unusedBallColor = {field ->
  for (def color : BALL_COLOR_VALUES) {
    if (!hasBallWithColor(field, color)) return color;
  }
  return null;
};

def allRolloversActiveForColor = {ballColor ->
  return centerRolloversByColor[ballColor].every {it.allRolloversActive()};
};

def updateCenterRollovers = {field ->
  BALL_COLOR_VALUES.each {ballColor ->
    def hasBall = hasBallWithColor(field, ballColor) || IGNORE_BALL_COLOR;
    centerRolloversByColor[ballColor].forEach {rollover ->
      rollover.setIgnoreBall(!hasBall);
      if (hasBall) {
        rollover.setVisible(true);
        rollover.setNewColor(WALL_COLORS[ballColor]);
      }
      else {
        def active = rollover.allRolloversActive();
        rollover.setVisible(active);
        if (active) {
          rollover.setNewColor(DISABLED_ROLLOVER_COLORS[ballColor]);
        }
      }
    };
  };
};

// Forward declaration since it's called by updateCenterLines.
def resetCenter = null;

def updateCenterLines = {field ->
  def allLinesVisible = true;
  BALL_COLOR_VALUES.each {ballColor ->
    def rollovers = centerRolloversByColor[ballColor];
    def lines = centerLinesByColor[ballColor];
    for (def i=0; i<rollovers.size(); i++) {
      def r1 = rollovers[i];
      def r2 = rollovers[i==rollovers.size()-1 ? 0 : i+1];
      def lineVisible = (r1.allRolloversActive() && r2.allRolloversActive());
      lines[i].setVisible(lineVisible);
      if (!lineVisible) allLinesVisible = false;
    }
  };
  if (allLinesVisible) {
    if (multiballStatus == MultiballStatus.ACTIVE) {
      multiballJackpotCount += 1;
      def prefix = (multiballJackpotCount > 1) ? multiballJackpotCount+"x " : "";
      field.showGameMessage(prefix + "Jackpot!", 3000);
      field.addScore((JACKPOT_BASE_SCORE * multiballJackpotCount).toLong());
      // This will make a recursive call to updateCenterLines,
      // but only one because all the lines will be hidden
      resetCenter(field);
    }
    else if (multiballStatus == MultiballStatus.NOT_READY) {
      field.showGameMessage("Shoot the pyramid!", 3000);
      multiballStatus = MultiballStatus.READY;
    }
  }
};

def checkForRamp = {field, ball, sensorId, ballColor ->
  if (sensorId == ball.getMostRecentSensorId()) {
    if (!hasBallWithColor(field, ballColor) && !allRolloversActiveForColor(ballColor)) {
      setBallColor(ball, ballColor);
      updateCenterRollovers(field);
    }
    def percentMultiplier = 100 + rampBonuses[ballColor];
    if (field.getBalls().size() > 1) {
      percentMultiplier *= 2;
    }
    def score = (RAMP_BASE_SCORE/100) * percentMultiplier;
    field.addScore(score.toLong());
  }
};

resetCenter = {field ->
  BALL_COLOR_VALUES.each {ballColor ->
    centerRolloversByColor[ballColor].each {rollover ->
      rollover.setRolloverActiveAtIndex(0, false);
    }
  }
  updateCenterRollovers(field);
  updateCenterLines(field);
};

def incrementRampBonus = {field, ballColor, name ->
  rampBonuses[ballColor] += 10;
  field.showGameMessage(name + " ramp bonus +" + rampBonuses[ballColor] + "%", 1500);  
};

def startMultiball = {field ->
  triangleWalls.each {wall -> wall.setRetracted(false);}
  triangleRotationSpeed = -2;
  resetCenter(field);
  field.removeBallWithoutBallLoss(field.getBalls()[0]);
  field.getFieldElementById("BallSaver-left").setRetracted(false);
  field.getFieldElementById("BallSaver-right").setRetracted(false);

  def doLaunch = {
    def ball = field.launchBall();
    setBallColor(ball, unusedBallColor(field));
    updateCenterRollovers(field);
  };

  // "Starting" state until the last ball is launched so we
  // don't exit multiball until then.
  multiballStatus = MultiballStatus.STARTING;
  multiballJackpotCount = 0;
  field.scheduleAction(1000, doLaunch);
  field.scheduleAction(4000, doLaunch);
  field.scheduleAction(7000, doLaunch);
  field.scheduleAction(10000, {
    doLaunch();
    multiballStatus = MultiballStatus.ACTIVE;
  });
};

def endMultiball = {field ->
  triangleRotationSpeed = 1;
  triangleCenterRollover.setRolloverActiveAtIndex(0, false);
  resetCenter(field);
  multiballStatus = MultiballStatus.NOT_READY;
};

def doExtraBall = {field ->
  field.addExtraBall();
  field.showGameMessage("Extra Ball!", 3000);
};

def resetExtraBallIfNeeded = {
  if (extraBallRollover.allRolloversActive()) {
    extraBallRollover.setRolloverActiveAtIndex(0, false);
    extraBallBarriers.each {wall -> wall.setRetracted(false);};
  }
};

// These methods are called for specific game events.
return [
  gameStarted: {field ->
    // Initialize common variables.
    launchBarrier = field.getFieldElementById("LaunchBarrier");

    triangleRotationAngle = TAU/4; // 
    triangleWalls = (1..3).collect {index ->
      field.getFieldElementById("TriangleWall" + index);
    };
    triangleCenterRollover = field.getFieldElementById("TriangleCenter");

    def getCenterRollovers = {color -> (1..3).collect {index ->
      field.getFieldElementById("CenterRollover_" + color + "_" + index);
    }};

    centerRolloversByColor = [
      (BallColor.BLUE): getCenterRollovers("Blue"),
      (BallColor.RED): getCenterRollovers("Red"),
      (BallColor.YELLOW): getCenterRollovers("Yellow"),
      (BallColor.GREEN): getCenterRollovers("Green"),
    ];

    def centerLineSuffixes = ['1_2', '2_3', '3_1'];
    def getCenterLines = {color -> centerLineSuffixes.collect {s ->
      field.getFieldElementById("CenterLine_" + color + "_" + s);
    }};
    centerLinesByColor = [
      (BallColor.BLUE): getCenterLines("Blue"),
      (BallColor.RED): getCenterLines("Red"),
      (BallColor.YELLOW): getCenterLines("Yellow"),
      (BallColor.GREEN): getCenterLines("Green"),
    ];

    rampBonuses = [:];
    BALL_COLOR_VALUES.each {c -> rampBonuses[c] = 0;}

    extraBallBarriers = [
      field.getFieldElementById("ExtraBallBarrier_Blue"),
      field.getFieldElementById("ExtraBallBarrier_Red"),
      field.getFieldElementById("ExtraBallBarrier_Yellow"),
      field.getFieldElementById("ExtraBallBarrier_Green"),
    ];
    extraBallRollover = field.getFieldElementById("ExtraBallRollover");

    updateCenterRollovers(field);
    updateCenterLines(field);
    multiballStatus = MultiballStatus.NOT_READY;
  },

  ballLost: {field ->
    updateCenterRollovers(field);
  },

  ballInSensorRange: {field, sensor, ball ->
    def sensorId = sensor.getElementId();
    // Check for retracting or restoring the launch barrier.
    if (sensorId == "LaunchBarrierSensor") {
      launchBarrier.setRetracted(false);
    }
    else if (sensorId == "LaunchBarrierRetract") {
      launchBarrier.setRetracted(true);
    }
    else if (sensorId == "RampSensor_OuterLeftTop") {
      checkForRamp(field, ball, "RampSensor_OuterRightTop", BallColor.GREEN);
    }
    else if (sensorId == "RampSensor_OuterRightTop") {
      checkForRamp(field, ball, "RampSensor_OuterLeftTop", BallColor.BLUE);
    }
    else if (sensorId == "RampSensor_LeftTop") {
      checkForRamp(field, ball, "RampSensor_LeftMiddle", BallColor.RED);
    }
    else if (sensorId == "RampSensor_RightTop") {
      checkForRamp(field, ball, "RampSensor_RightMiddle", BallColor.YELLOW);
    }
    else if (sensorId == "Sensor_ExtraBallExit") {
      resetExtraBallIfNeeded();
    }
  },

  allDropTargetsInGroupHit: {field, targetGroup, ball ->
    // Activate ball saver for left and right groups.
    def id = targetGroup.getElementId();
    if (id == "DropTargetLeftSave") {
      field.getFieldElementById("BallSaver-left").setRetracted(false);
      field.showGameMessage("Left Save Enabled", 1500);
    }
    else if (id == "DropTargetRightSave") {
      field.getFieldElementById("BallSaver-right").setRetracted(false);
      field.showGameMessage("Right Save Enabled", 1500);
    }
    else if (id == "DropTargets_BlueRamp") {
      incrementRampBonus(field, BallColor.BLUE, "Blue");
    }
    else if (id == "DropTargets_RedRamp") {
      incrementRampBonus(field, BallColor.RED, "Red");
    }
    else if (id == "DropTargets_YellowRamp") {
      incrementRampBonus(field, BallColor.YELLOW, "Yellow");
    }
    else if (id == "DropTargets_GreenRamp") {
      incrementRampBonus(field, BallColor.GREEN, "Green");
    }
  },

  allRolloversInGroupActivated: {field, rollovers, ball ->
    def id = rollovers.getElementId();
    if (id == "FlipperRollovers") {
      field.incrementAndDisplayScoreMultiplier(1500);
      rollovers.setAllRolloversActivated(false);
    }
    else if (rollovers == triangleCenterRollover) {
      // Should always be ready, but just a sanity check.
      if (multiballStatus == MultiballStatus.READY) {
        startMultiball(field);
      }
    }
    else if (rollovers == extraBallRollover) {
      doExtraBall(field);
    }
    updateCenterLines(field);
  },

  processCollision: {field, element, body, ball ->
    if (multiballStatus == MultiballStatus.READY) {
      if (triangleWalls.contains(element)) {
        element.setRetracted(true);
      }
    }
  },

  isFieldActive: {field -> return true;},

  tick: {field, nanos ->
    if (triangleWalls!=null && triangleRotationSpeed!=0) {
      triangleRotationAngle +=
          (triangleRotationSpeed * triangleRotationBaseSpeed) * nanos/1e9;
      if (triangleRotationAngle < 0) triangleRotationAngle += TAU;
      if (triangleRotationAngle >= TAU) triangleRotationAngle -= TAU;
      def a0 = triangleRotationAngle;
      def a1 = triangleRotationAngle + TAU/3;
      def a2 = triangleRotationAngle - TAU/3;
      def x0 = (triangleCenter[0] + triangleRadius*Math.cos(a0)).toFloat();
      def y0 = (triangleCenter[1] + triangleRadius*Math.sin(a0)).toFloat();
      def x1 = (triangleCenter[0] + triangleRadius*Math.cos(a1)).toFloat();
      def y1 = (triangleCenter[1] + triangleRadius*Math.sin(a1)).toFloat();
      def x2 = (triangleCenter[0] + triangleRadius*Math.cos(a2)).toFloat();
      def y2 = (triangleCenter[1] + triangleRadius*Math.sin(a2)).toFloat();
      // change wall position?
      triangleWalls[0].setStartAndDirection(x0, y0, x1, y1);
      triangleWalls[1].setStartAndDirection(x1, y1, x2, y2);
      triangleWalls[2].setStartAndDirection(x2, y2, x0, y0);
    }

    // Check for exiting multiball.
    if (field.getBalls().size()<=1 && multiballStatus == MultiballStatus.ACTIVE) {
      endMultiball(field);
    }
  },
]
